const dealData = $input.item.json.message.content;

// Get original context from previous node's output
const context = $('Parse Fields').item.json.body;

// Merge the parsed deal data with the original context
return { json: { ...context, dealData } };

const deals = $input.item.json.message;

if (!deals || deals.length === 0) {
  // Handle case where no deals exist (use a limited, safe list of fields for the AI)
  const safeFields = ['name', 'deal_type', 'deal_value', 'status', 'owner'];
  const fields = safeFields.map(key => ({
    name: key,
    label: key
  }));

  // Add all original input data back for the next nodes
  return { json: { fields, body: $('Webhook').item.json.body } };
}

const firstDeal = deals[0];
const fields = Object.keys(firstDeal).map(key => ({
  name: key,
  label: key
}));

// Add all original input data back for the next nodes
return { json: { fields, body: $('Webhook').item.json.body } };

const dealData = $json.dealData;
const chatId = $json.chatId;

if (!dealData || !chatId) {
  throw new Error('Missing dealData or chatId');
}

// Helper function to format values for Telegram
function formatValue(value) {
  if (Array.isArray(value)) {
    return value.map((item) => {
      if (typeof item === 'object' && item !== null) {
        // Format nested objects like tasks/notes
        return `\n    - ${Object.entries(item).map(([k, v]) => `*${k}:* ${v}`).join(', ')}`;
      }
      return `\n    - ${item}`;
    }).join('');
  } else if (typeof value === 'object' && value !== null) {
    return Object.entries(value).map(([k, v]) => `*${k}:* ${v}`).join(', ');
  }
  return value;
}

// Format the main properties
const fieldsList = Object.entries(dealData)
  .map(([key, value]) => `• *${key.split('_').map(w => w[0].toUpperCase() + w.slice(1)).join(' ')}:* ${formatValue(value) || '—'}`)
  .join('\n');

function uuidv4() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    const r = Math.random() * 16 | 0;
    const v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

const draftId = uuidv4();
const isUpdateText = $json.isUpdate ? `*\n\n[UPDATING DEAL: ${$json.docName}]*` : '';

const telegramPayload = {
  chat_id: chatId,
  text: `*Draft Deal*${isUpdateText}\n\n${fieldsList || 'No data'}\n\nConfirm?`,
  parse_mode: 'Markdown',
  reply_markup: {
    inline_keyboard: [
      [{ text: 'Confirm', callback_data: `confirm_deal_draft:${draftId}` }],
      [{ text: 'Cancel', callback_data: 'cancel_draft' }]
    ]
  },
  // Data to store and pass to the final confirmation webhook
  draftId: draftId,
  dealData: dealData,
  isUpdate: $json.isUpdate || false,
  docName: $json.docName || null, 
  crmBaseUrl: $json.crmBaseUrl,
  frappeApiKey: $json.frappeApiKey,
  frappeApiSecret: $json.frappeApiSecret,
  chatId: $json.chatId
};

// If you want to debug the reply_markup as a string, uncomment the next line:
// telegramPayload.reply_markup_debug = JSON.stringify(telegramPayload.reply_markup, null, 2);

return { json: telegramPayload };

let dealData = $json.dealData;

const pretty = Object.entries(dealData)
  .map(([k, v]) => {
    const label = k.split('_').map(w => w[0].toUpperCase() + w.slice(1)).join(' ');
    // This is now redundant as formatting happens in Format Telegram Message1
    return `• *${label}:* ${v || '—'}`;
  })
  .join('\n');

// We rely on the formatting done in 'Format Telegram Message1' 
// to build the final message in StoreDraft Data1

return {
  json: $json
};
